import React, { useRef } from 'react'
import { useState } from 'react';
import { useEffect } from 'react'
import {useParams} from 'react-router-dom'
import Loading from './Loading';
import RecipeCard from './RecipeCard';

// import useFetch from './Utils/Fetch';
import Get from './Utils/Get';



//A collection of keys are added to maintain uptime
// fa37f0fee335472a906bdc5a0cb4c3a8
// b996787c904f49ba9142544aa5832a3e 
// 1882448aefc44b3ea30609cdccf40951 


function Recipe(props) {  
    const [idPage, setidPage] = useState(null);
    const { id } = useParams();
    const [pageDetails, setpageDetails] = useState({'url' : ``, 'type': ``})


    useEffect(() =>{
            setidPage(null)
            setpageDetails(null)
            const keys = [
              '1882448aefc44b3ea30609cdccf40951',
              '30a751e2874b43f1ad3ecaee77f90358',
              '0196cdecde9e4bc9b480c4aa600c5da0',
              '745e795c3f0c4208bca68eb1cee6b5cc',
              'dace1d4c4a18413aaae4c957cd7c06da',
              '281e19f37bff48b6a40aebba8c5c3daa',
              '0888534ce1d745669ac10e33a796231b',
              '9ec50c8497ff444ebc23caff064ecb84',
              '2d2179c4d6a84cecb45f33eb7c7a7fcd',
              'ab2ba4413ae64f3983f87fae07bf602d',
              '67f51a29ba7d4d17a05abfe0cd981d80',
              'b996787c904f49ba9142544aa5832a3e',
              'bdd064e1a1d64716b37ff9b6368a03ad',
              'c192100da37d45f7a623175aa6541583',
              '8b9f348c4c774156b9e7c50e428c7ca4',
              'b63ef92116bd4190bef73a4889f032d9',
              'cff3befae479438f8f9dfffcbe5be535',
              'a20b5632aa8d489e82a17c2b6b5c57b9',
              'bdd064e1a1d64716b37ff9b6368a03ad',
              'ebf26298c85c4ab9a16fdcd10992f311',
              'fa37f0fee335472a906bdc5a0cb4c3a8'
                ]
            let key = keys[Math.floor((Math.random() * 20))]

            //Setter section - Set URL request parameters - Everytime URL changes
            if (!id){
                setidPage(false);
                switch(props.type) {
                    case "African":
                      setpageDetails({'url' : `https://api.spoonacular.com/recipes/complexSearch?query=african&number=150&apiKey=${key}`, type : 'Page'})
                      break;

                    case "American":
                      setpageDetails({'url' : `https://api.spoonacular.com/recipes/complexSearch?query=american&number=150&apiKey=${key}`, type : 'Page'})
                      break;

                    case "Chinese":
                      setpageDetails({'url' : `https://api.spoonacular.com/recipes/complexSearch?query=chinese&number=150&apiKey=${key}`, type : 'Page'})
                      break;

                    case "European":
                      setpageDetails({'url' : `https://api.spoonacular.com/recipes/complexSearch?query=european&number=150&apiKey=${key}`, type : 'Page'})
                      break;

                    case "Spanish":
                      setpageDetails({'url' : `https://api.spoonacular.com/recipes/complexSearch?query=spanish&number=150&apiKey=${key}`, type : 'Page'})
                      break;

                    default:
                      break;
                  }    
            }

          if (id){            
                    setidPage(true);
                    setpageDetails({url : `https://api.spoonacular.com/recipes/${id}/information?includeNutrition=true&apiKey=${key}`, type: `Id`})        
          }

    }, [props,id])      


    const {data, loading} = Get(pageDetails) 



if (!idPage) {

  return (
    <div className={''}>

        <div className={' h-full w-full grid mt-64 md:mt-32 lg:grid-cols-4 md:grid-cols-2 gap-10'}>
            {
                loading  ? <Loading/> : Array.isArray(data) && data.map( rec => {
                    return (
                            <RecipeCard key={rec.id} id={rec.id} title={rec.title} summary={rec.summary} cheap={rec.cheap} diets={rec.diets} image={rec.image} />
                    )
                  }
                )
            } 
        </div>
    </div>

  )
} 
else if (idPage) {
  // const TextDivision = document.createElement('div')
  // TextDivision.innerHTML = data.summary;
  // 
  //
      return (

      <div>

        { loading ? <Loading/> : 

           data.image ?  
           <div className={'w-full h-fit grid lg:grid-cols-2 md:gap-11 md:pt-28 mt-56 lg:mt-3 mb-12 lg:ml-8 rounded bg-gray-100'}>

                  <div className={'flex flex-col h-full justify-center items-center bg-blue-30 p-7'}> 
                      <div className={'flex-auto'}>
                            <img src={data.image} alt={data.title} className={'rounded w-full shadow-lg mx-auto transform  transition ease-in duration-500'}/>

                              <span className={'text-white text-lg font-bold uppercase bg-pink-800 rounded-full absolute mt-3 ml-3 p-2 top-0  '}>
                                {data.readyInMinutes ? data.readyInMinutes + ' Mins' : ''}
                              </span>

                      </div>

                      <div className={'w-full  font-extrabold text-3xl text-rose-600 opacity-90 text-center pt-12'}>{data.title}</div>                  

                      <div className={'p-5 pb-8 w-full justify-around items-center text-amber-900 flex flex-wrap'}>
                          <div className={'h-fit p-4 m-2 text-center shadow flex flex-wrap justify-center'}>
                              {
                                data.nutrition.ingredients ? 
                                      data.nutrition.ingredients.map( ingredient => {
                                        return (
                                          <span className={ 'odd:text-sky-900 pl-4 pr-4 pb-4 uppercase'}>{ingredient.name}</span> 
                                        )
                                      }
                                      )
                                    : <div>failed to get ingredients...</div>
                              } 
                          </div>
                          <span className={' text-center md:text-left p-4 h-fit shadow'}>{'HealthScore ' + data.healthScore}</span>

                          <div className={'h-fit p-4 m-2 text-center shadow'}>
                              {
                                data.cuisines ? 
                                      data.cuisines.map( cuisine => {
                                        return (
                                          <span className={ ' pl-4 pr-4 pb-4 text-3xl uppercase'}>{ cuisine }</span> 
                                        )
                                      }
                                      )
                                    : <div>failed to get ingredients...</div>
                              } 
                            </div>  
                          <span className={'h-fit text-center md:text-right p-4 shadow'}>{'CostPrice $' + data.pricePerServing}</span>
                      </div>

                  </div>

                  <section className={'bg-gray-50 h-full w-fit justify-around items-center border-l border-t border-b shadow-sm pb-2 flex flex-col rounded mr-4'}>
                        <div className={'text-white antialiased font-extralight hover:subpixel-antialiased text-center content-center italic z-10 shadow-lg  bg-myblack h-fit pt-10 pb-10 pl-2'}>
                        {
                          data.analyzedInstructions[0].steps ? 

                                    data.analyzedInstructions[0].steps.map( ({step}) => {
                                          return (
                                              <div className={'block odd:bg-mymagenta p-8 text-center'}>{step}</div>
                                                )
                                      }
                                              )

                          : 
                                <div>failed!!!!!!!</div>
                        }
                        </div>

                        <div className={'font-serif shadow border-t  rounded bg-sky-200 p-7 pt-20 h-fit lg:w-2/3 mt-5 lg:mt-32 relative'}>
                        <span className={'text-white text-lg font-bold uppercase bg-pink-600 rounded-full absolute top-0 mt-3 p-2  '}>
                            Conclusion
                          </span>
                          <h3  dangerouslySetInnerHTML={{__html: data.summary}} ></h3>
                        </div>
                  </section>

           </div> 
           : <Loading/>

        }

      </div> 
      )
} 



          }

export default Recipe
