                                                }
                                                }
                                            )


                                    }    

                                break;

                                case "Id":

                                        console.log('Getting New Fetch By Recipe' + url )
                                        const res = fetch(url) 
                                        .then( res=> res.json() )
                                        .then(
                                            result=>
                                            {    
                                                console.log(result)                                            
                                                setReciept({'data': result,'loading': false})
                                                console.log(reciept);                                                    
                                            }
                                        )



                                break;

                                case "Page":

                                        console.log('Getting New Fetch for Page')
                                        const response = fetch(url) 
                                        .then( res=> res.json()  )
                                        .then(
                                            res=>
                                            {
                                                console.log(res)
                                            if (res.results){
                                                console.log(res.results)
                                                setReciept({'data': res.results, 'loading': false})
                                                console.log(reciept);
                                            }
                                            }
                                        )


                                break;
                                default:
                                // code block
                            }



  }, [url])

  return reciept
}



export default Get
