import React from 'react'
import { useState } from 'react';
import { useEffect } from 'react'
import {useParams} from 'react-router-dom'
import useFetch from './Utils/Fetch';


function Recipe(props) {  
    const { id } = useParams();
    const [pageDetails, setpageDetails] = useState()

    setTimeout(() => {
      const {data, loading} = useFetch('https://api.spoonacular.com/recipes/random?number=150&apiKey=fa37f0fee335472a906bdc5a0cb4c3a8');
    }, 3000);

    useEffect(() =>{
    if (props.type){
        switch(props.type) {
            case "African":
              setpageDetails("African")
              break;

            case "American":
              setpageDetails("American")
              break;

            case "Asian":
              setpageDetails("Asian")
              break;

            default:
              break;
          }    
    }

    if (id){                
        setpageDetails(id)
      }

      return(()=>{
          setpageDetails(null)
      })
    }, [[],pageDetails, id,props.type])      

  return (
    <div className={'container section'}> 
        <div className='card-body'>
            <div className={'card'}>
            {pageDetails}
            </div>
        </div>
    </div>
  )
}

export default Recipe
