import React, { useState } from 'react'

function Search() {
    const [Input, SetInput] = useState(null);

   const handleChange = (evt) => {
        evt.preventDefault()
        SetInput(evt.target.value)
    }


  return (
    <div className={' p-4 lg:pt-7 md:mr-auto'}>
    <input 
    type = 'text'
    placeholder='recipe'
    onChange={handleChange}

    />
    </div>
  )
}

export default Search
