import React, { useEffect, useState } from 'react'
import RecipeCard from './RecipeCard';

function Recipes() 
{

  const [recipes, setRecipes] = useState([]);
  const [LoadingRecipes, SetLoadingRecipes] = useState(true);

  useEffect( ()=> { 
    const FetchRecipes = async function () {
      let response = await fetch(`https://api.sponacular.com/recipes/random?number=15&apiKey=8ff48abf158e43cdbe97e9f94af44be3`);
      let result =  await response.json().then( res => {
           setRecipes(res.recipes);
      } )
      .catch(() => {
        console.log("error")
      })

    }

     FetchRecipes(); 
     SetLoadingRecipes(false);

  }, []);


  return (
    <div>
     {
        LoadingRecipes && <Loading/>,
        !LoadingRecipes && recipes && recipes.map( rec => {
          return (
            <RecipeCard />
          )
          }
        )
     } 
    </div>
  )
}

export default Recipes
