import React, { useEffect, useState } from 'react'
import RecipeCard from './RecipeCard';
import Loading from './Loading';

function Recipes() 
{

  const [recipes, setRecipes] = useState([]);
  const [LoadingRecipes, SetLoadingRecipes] = useState(true);

  useEffect( ()=> { 
    const FetchRecipes = async function () {
      let response = await fetch(`https://api.spoonacular.com/recipes/random?number=15&apiKey=1882448aefc44b3ea30609cdccf4095`);
      let result =  await response.json().then( res => {
          if (res.status === 'failure'){
            console
          }
           setRecipes(res.recipes);
      } )
      .catch(() => {
        console.log("error")
      })
    }  
     FetchRecipes()

  }, []);

  useEffect( ()=>{
    if (recipes.length > 1){
      SetLoadingRecipes(false);
      }
  }, [recipes])


  return (
    <div>
     {
        LoadingRecipes  ? <Loading/> : recipes.map( rec => {
          return (
            <div key={rec.id} className={'card-group'}>

                   <RecipeCard  title={rec.title} summary={rec.summary} cheap={rec.cheap} diets={rec.diets} image={rec.image} />

            </div>
          )
          }
        )
     } 
    </div>
  )
}



export default Recipes
