import React, { useState } from 'react'
import { useRef } from 'react'

function Search() {
    const [Input, SetInput] = useState(null);

   const handleChange = (e) => {
        e.preventDefault()
        SetInput(e.target.value)
    }


  return (
    <>
    <input 
    type = 'text'
    placeholder='recipe'
    onChange={handleChange()}

    />
    </>
  )
}

export default Search
