import React, { useState } from 'react'

function Search() {
    const [Input, SetInput] = useState(null);

   const handleChange = (evt) => {
        evt.preventDefault()
        SetInput(evt.target.value)
    }


  return (
    <div className={'md:block p-2'}>
        <div className={'flex justify-center'}>
          <div className={' xl:w-96'}>
            <div className={'input-group relative flex items-stretch w-full'}>
              <input type="search" placeholder='Search recipe' className={'form-control font-nav relative flex-auto min-w-0 block w-full px-3 py-1 text-base text-gray-700 bg-white bg-clip-padding border border-solid border-gray-300 rounded-l transition ease-in-out m-0 focus:text-gray-700 focus:bg-white focus:border-gray-100 focus:outline-none'}/>
              <button className={'btn bg-gray-500 text-white font-medium text-xs  uppercase hover:bg-gray-900 focus:bg-gray-100  focus:outline-none focus:ring-0 active:bg-gray-100 active:shadow-lg transition duration-150 ease-in-out flex items-center'} type="button" id="button-addon2">
                <svg aria-hidden="true" focusable="false" data-prefix="fas" data-icon="search" className={'w-4'} role="img" xmlns="http://www.w3.org/2000/svg" viewBox="0 0 512 512">
                  <path fill="currentColor" d="M505 442.7L405.3 343c-4.5-4.5-10.6-7-17-7H372c27.6-35.3 44-79.7 44-128C416 93.1 322.9 0 208 0S0 93.1 0 208s93.1 208 208 208c48.3 0 92.7-16.4 128-44v16.3c0 6.4 2.5 12.5 7 17l99.7 99.7c9.4 9.4 24.6 9.4 33.9 0l28.3-28.3c9.4-9.4 9.4-24.6.1-34zM208 336c-70.7 0-128-57.2-128-128 0-70.7 57.2-128 128-128 70.7 0 128 57.2 128 128 0 70.7-57.2 128-128 128z"></path>
                </svg>
              </button>
            </div>
          </div>
        </div>
    </div>
  )
}

export default Search

