import { useState, useEffect } from 'react'

function Get({ url, type,}) {
  const [reciept, setReciept] = useState({'data' : null, 'loading' : true})
  const savedRecipes = localStorage.getItem("Recipes");

  useEffect( () =>  {       
                            switch(type) {
                                case 'Homepage':
                                    if (savedRecipes) 
                                    { 
                                        setTimeout(() => {
                                                console.log("Reading Old Fetch")
                                                console.log(savedRecipes);
                                                const Recipe = JSON.parse(savedRecipes);
                                                setReciept({'data': (Recipe), 'loading': false})
                                        }, 1000);

                                    }

                                    else 
                                    {
                                            setTimeout(() => {
                                            console.log('Getting New Fetch')
                                            const response = fetch(url) 
                                            .then( res=> res.json()  )
                                            .then(
                                                res=>
                                                {
                                                if (res.recipes){
                                                    setReciept({'data': res.recipes, 'loading': false})
                                                    localStorage.setItem("Recipes", JSON.stringify(res.recipes))
                                                }
                                                }
                                            )
                                            }, 1000);

                                    }    

                                break;

                                case "Id":
                                    setTimeout(() => {
                                        console.log('Getting New Fetch')
                                        const response = fetch(url) 
                                        .then( res=> res.json()  )
                                        .then(
                                            res=>
                                            {
                                            if (res.recipes){
                                                setReciept({'data': res.recipes, 'loading': false})
                                                localStorage.setItem("Recipes", JSON.stringify(res.recipes))
                                            }
                                            }
                                        )
                                        }, 1000);
                                break;

                                case "Page":
                                    setTimeout(() => {
                                        console.log('Getting New Fetch')
                                        const response = fetch(url) 
                                        .then( res=> res.json()  )
                                        .then(
                                            res=>
                                            {
                                            if (res.recipes){
                                                setReciept({'data': res.recipes, 'loading': false, 'displayPage' : true})
                                                localStorage.setItem("Recipes", JSON.stringify(res.recipes))
                                            }
                                            }
                                        )
                                        }, 1000);

                                default:
                                // code block
                            }



  }, [savedRecipes, url, type])
  console.log(url, type);
  return reciept

}

export default Get
