import React, { useState } from "react";

export default function Message () {
  let [message, setMessage] = useState("");

  return (
    <div>
      <input
        type="text"
        value={message}
        placeholder="Enter some letters"
        onChange={e => {
          const val = e.target.value;
          setMessage(message += val);
        } }
      />
      <p>
        <strong>{message}</strong>
      </p>
    </div>
  );
};
