                switch(props.type) {
                    case "African":
                      setpageDetails({'url' : "https://api.spoonacular.com/recipes/complexSearch?query=african&number=150&apiKey=1882448aefc44b3ea30609cdccf40951", type : 'Page'})
                      break;

                    case "American":
                      setpageDetails({'url' : "https://api.spoonacular.com/recipes/complexSearch?query=american&number=150&apiKey=1882448aefc44b3ea30609cdccf40951", type : 'Page'})
                      break;

                    case "Chinese":
                      setpageDetails({'url' : "https://api.spoonacular.com/recipes/complexSearch?query=chinese&number=150&apiKey=1882448aefc44b3ea30609cdccf40951", type : 'Page'})
                      break;

                    case "European":
                      setpageDetails({'url' : "https://api.spoonacular.com/recipes/complexSearch?query=european&number=150&apiKey=1882448aefc44b3ea30609cdccf40951", type : 'Page'})
                      break;

                    case "Spanish":
                      setpageDetails({'url' : "https://api.spoonacular.com/recipes/complexSearch?query=spanish&number=150&apiKey=882448aefc44b3ea30609cdccf40951", type : 'Page'})
                      break;

                    default:
                      break;
                  }    
            }

          if (id){            
                    setidPage(true);
                    setpageDetails({url : `https://api.spoonacular.com/recipes/${id}/information?includeNutrition=true&apiKey=1882448aefc44b3ea30609cdccf40951`, type: `Id`})        
          }

    }, [id])      


    const {data, loading} = Get(pageDetails) 



if (!idPage) {
