import React from 'react'
import { useState } from 'react';
import { useEffect } from 'react'
import {useParams} from 'react-router-dom'


function Recipe(props) {  
    const {id} = useParams();
    const [pageDetails, setpageDetails] = useState()

    useEffect(() =>{
    if (props.type){
        switch(props.type) {
            case "African":
              setpageDetails("African")
              break;

            case "American":
              setpageDetails("American")
              break;

            case "Asian":
              setpageDetails("Asian")
              break;

            default:
              break;
          }    
    }

    if (id){                
        setpageDetails(id)
      }

      return(()=>{
          setpageDetails(null)
      })
    }, [pageDetails, id,props.type])      

  return (
    <div className={'container section'}> 
        <div className='card-body'>
            <div className={'card'}>
            {pageDetails}
            </div>
        </div>
    </div>
  )
}

export default Recipe
