import  React from 'react'
import RecipeCard from './RecipeCard';
import RecipeColumn from './RecipeColumn';
import Loading from './Loading';
// import useFetch from './Utils/Fetch';c
import Get from './Utils/Get';

function HomeRecipes() 
{

          const keys = ['1882448aefc44b3ea30609cdccf40951', 'b996787c904f49ba9142544aa5832a3e', 'fa37f0fee335472a906bdc5a0cb4c3a8']
          let key = keys[Math.floor((Math.random() * 3))]   
          const {data, loading} = Get({'url': `https://api.spoonacular.com/recipes/random?number=23&apiKey=${key}`, type : 'Homepage'});   

  return (

    <div className={' h-full w-full pt-40 md:pt-40 grid items-stretch content-baseline lg:grid-cols-4 md:grid-cols-2  auto-cols-max grid-flow-row gap-10'}>
              {
                  loading  ? <Loading/> : data.map( rec => {

                              let y = Math.floor((Math.random() * 2))

                              switch (y) {

                              case 0:
                                return (
                                   <RecipeCard key={rec.id} id={rec.id} title={rec.title} summary={rec.summary} cheap={rec.cheap} diets={rec.diets} image={rec.image} />
                                ) 


                              case 1:
                                return (
                                  <RecipeColumn key={rec.id} id={rec.id} title={rec.title} summary={rec.summary} cheap={rec.cheap} diets={rec.diets} image={rec.image} />
                                ) 



                              }



                    }
                  )} 
    </div>
  )
}



export default HomeRecipes
