import React, { useEffect, useState } from 'react'
import RecipeCard from './RecipeCard';
import Loading from './Loading';

function Recipes() 
{

  const [recipes, setRecipes] = useState([]);
  const [LoadingRecipes, SetLoadingRecipes] = useState(true);

  useEffect( ()=> { 
    const FetchRecipes = async function () {
      let response = await fetch(`https://api.spoonacular.com/recipes/random?number=15&apiKey=8ff48abf158e43cdbe97e9f94af44be3`);
      let result =  await response.json().then( res => {
           setRecipes(res.recipes);
           SetLoadingRecipes(false);

      } )
      .catch(() => {
        console.log("error")
      })

    }

     FetchRecipes()

  }, []);


  return (
    <div>
     {
        LoadingRecipes  ? <Loading/> : recipes.map( rec => {
          return (

            <RecipeCard key={rec.id} title={rec.title} summary={rec.summary} cheap={rec.cheap} diets={rec.diets} image={rec.image} />
          )
          }
        )
     } 
    </div>
  )
}



export default Recipes
