import React from 'react'
import { useState } from 'react';
import { useEffect } from 'react'
import {useParams} from 'react-router-dom'
import Loading from './Loading';
import RecipeCard from './RecipeCard';

// import useFetch from './Utils/Fetch';
import Get from './Utils/Get';


function Recipe(props) {  
    const [idPage, setidPage] = useState(null);
    const { id } = useParams();
    const [pageDetails, setpageDetails] = useState({'url' : ``, 'type': ``})

    useEffect(() =>{
            if (!id){
                setidPage(false);
                switch(props.type) {
                    case "African":
                      setpageDetails({'url' : "https://api.spoonacular.com/recipes/complexSearch?query=african&number=150&apiKey=b996787c904f49ba9142544aa5832a3e", type : 'Page'})
                      break;

                    case "American":
                      setpageDetails({'url' : "https://api.spoonacular.com/recipes/complexSearch?query=american&number=150&apiKey=b996787c904f49ba9142544aa5832a3e", type : 'Page'})
                      break;

                    case "Chinese":
                      setpageDetails({'url' : "https://api.spoonacular.com/recipes/complexSearch?query=chinese&number=150&apiKey=b996787c904f49ba9142544aa5832a3e", type : 'Page'})
                      break;

                    case "European":
                      setpageDetails({'url' : "https://api.spoonacular.com/recipes/complexSearch?query=european&number=150&apiKey=b996787c904f49ba9142544aa5832a3e", type : 'Page'})
                      break;

                    case "Spanish":
                      setpageDetails({'url' : "https://api.spoonacular.com/recipes/complexSearch?query=spanish&number=150&apiKey=882448aefc44b3ea30609cdccf40951", type : 'Page'})
                      break;

                    default:
                      break;
                  }    
            }

          if (id){            
                    setidPage(true);
                    setpageDetails({url : `https://api.spoonacular.com/recipes/${id}/information?includeNutrition=true&apiKey=b996787c904f49ba9142544aa5832a3e`, type: `Id`})        
          }

    }, [props])      


    const {data, loading} = Get(pageDetails) 

    function EmptyObject (value) {
      return value && Object.keys(value).length === 0 && value.constructor === Object;
    }

if (!idPage) {
  return (
    <div className={''}>

        <div className={' h-full w-full grid lg:grid-cols-4 md:grid-cols-2 gap-10'}>
            {
                loading  ? <Loading/> : Array.isArray(data) && data.map( rec => {
                    return (
                            <RecipeCard key={rec.id} id={rec.id} title={rec.title} summary={rec.summary} cheap={rec.cheap} diets={rec.diets} image={rec.image} />
                    )
                  }
                )
            } 
        </div>
    </div>

  )
} 
else if (idPage) {
  return (
  <div>
    {
      loading ? <Loading/> :
       <div className='min-h-screen bg-stone-900 grid grid-cols-1 sm:grid-cols-2 lg:grid-cols-3 '>
        <div className=' bg-yellow-50  '>connected</div>
        <div className='bg-slate-300 '>connected</div>
        <div className='bg-amber-200 '>connected</div>
        <div className='bg-emerald-5'>connected</div>
        <div className='bg-blue-700 w-16'>connected</div>
        <div className='bg-rose-900 w-16'>connected</div>
        <div className='bg-yellow-900 w-16'>connected</div>
        <div className='bg-yellow-900 w-16'>connected</div>
        <div className='bg-yellow-900 w-16'>connected</div>
      </div>
    }
  </div>
  )
} 



          }

export default Recipe
