                            setReciept({'data':JSON.parse(savedRecipe), 'loading': false})
                            }

                        else 
                            {
                                console.log('here')

                                const response = fetch(url) .then( res=> res.json()).then(res=> console.log(res.recipes))

                                //         localStorage.setItem('Recipes', JSON.stringify(response.recipes))
                                //         setReciept({'data': response.recipes, 'loading': false})

                            }

  }, [url])

  return reciept

}

