import React, { useRef } from 'react'
import { useState } from 'react';
import { useEffect } from 'react'
import {useParams} from 'react-router-dom'
import Loading from './Loading';
import RecipeCard from './RecipeCard';

// import useFetch from './Utils/Fetch';
import Get from './Utils/Get';



//A collection of keys are added to maintain uptime
// fa37f0fee335472a906bdc5a0cb4c3a8
// b996787c904f49ba9142544aa5832a3e 
// 1882448aefc44b3ea30609cdccf40951 


function Recipe(props) {  
    const [idPage, setidPage] = useState(null);
    const { id } = useParams();
    const [pageDetails, setpageDetails] = useState({'url' : ``, 'type': ``})

    useEffect(() =>{

            const keys = [
              '1882448aefc44b3ea30609cdccf40951',
              '30a751e2874b43f1ad3ecaee77f90358',
              '281e19f37bff48b6a40aebba8c5c3daa',
              '9ec50c8497ff444ebc23caff064ecb84',
              'b996787c904f49ba9142544aa5832a3e',
              'fa37f0fee335472a906bdc5a0cb4c3a8'
                ]
            let key = keys[Math.floor((Math.random() * 6))]
            if (!id){
                setidPage(false);
                switch(props.type) {
                    case "African":
                      setpageDetails({'url' : `https://api.spoonacular.com/recipes/complexSearch?query=african&number=150&apiKey=${key}`, type : 'Page'})
                      break;

                    case "American":
                      setpageDetails({'url' : `https://api.spoonacular.com/recipes/complexSearch?query=american&number=150&apiKey=${key}`, type : 'Page'})
                      break;

                    case "Chinese":
                      setpageDetails({'url' : `https://api.spoonacular.com/recipes/complexSearch?query=chinese&number=150&apiKey=${key}`, type : 'Page'})
                      break;

                    case "European":
                      setpageDetails({'url' : `https://api.spoonacular.com/recipes/complexSearch?query=european&number=150&apiKey=${key}`, type : 'Page'})
                      break;

                    case "Spanish":
                      setpageDetails({'url' : `https://api.spoonacular.com/recipes/complexSearch?query=spanish&number=150&apiKey=${key}`, type : 'Page'})
                      break;

                    default:
                      break;
                  }    
            }

          if (id){            
                    setidPage(true);
                    setpageDetails({url : `https://api.spoonacular.com/recipes/${id}/information?includeNutrition=true&apiKey=${key}`, type: `Id`})        
          }

    }, [props,id])      


    const {data, loading} = Get(pageDetails) 
    const Description = useRef()


if (!idPage) {

  return (
    <div className={''}>

        <div className={' h-full w-full grid lg:grid-cols-4 md:grid-cols-2 gap-10'}>
            {
                loading  ? <Loading/> : Array.isArray(data) && data.map( rec => {
                    return (
                            <RecipeCard key={rec.id} id={rec.id} title={rec.title} summary={rec.summary} cheap={rec.cheap} diets={rec.diets} image={rec.image} />
                    )
                  }
                )
            } 
        </div>
    </div>

  )
} 
else if (idPage) {
  // const TextDivision = document.createElement('div')
  // TextDivision.innerHTML = data.summary;
  return (
   <div>
     { loading ? <Loading/> : 
          <div className={'w-full h-screen sm:grid grid-cols-2 gap-3 sm:mt-28 sm:ml-8 sm:p-10 bg-gray-100'}>
              <div className={' wflex flex-col items-start bg-blue-30 p-7'}> 
                  <img src={data.image} alt={data.title}/>
                  <div>{data.title}</div>
              </div>

              <section className={'bg-white w-2/3 flex flex-col sm:-mr-8 lg:-ml-20 justify-evenly'}>

                  <section className={'bg-red-400 p-7 '}>How TO lorem 
                  You can never have too many European recipes, so give Spanish Meatballs In Tomato Sauce a try. This recipe serves 20. One serving contains 137 calories, 6g of protein, and 9g of fat. For 60 cents per serving, this recipe covers 6% of your daily requirements of vitamins and minerals. 11 person have made this recipe and would make it again. From preparation to the plate, this recipe takes around 45 minutes. This recipe from Foodista requires parsley, sandwich bread, salt and pepper, and egg. It works well as a hor d'oeuvre. It is a good option if you're following a dairy free diet. All things considered, we decided this recipe deserves a spoonacular score of 28%. This score is not so tremendous. Try Spanish Meatballs In Tomato Sauce, Spanish Style Lamb Meatballs with Spicy Tomato Sauce, and Spanish Meatballs with Almond Sauce for similar recipes.
                  </section>

                  <div className={'bg-sky-200 p-7 '}>
                    <h3  dangerouslySetInnerHTML={{__html: data.summary}} ></h3>
                  </div>
              </section>

          </div>
    }
  </div> 
  )
} 



          }

export default Recipe
