import { Link } from "react-router-dom";

function RecipeColumn({title, id, summary, cheap, diets, image}) {
  return ( 

    <div className={`w-full justify-items-start bg-orange-700 lg:grid-flow-ro lg:col-span-2 border-l border-t border-b font-Nunito  border-r transform hover:scale-110 hover:shadow-lg transition ease-in duration-200 shadow text-black rounded  overflow-hidden space-x-2 font-mono`}>
    <div className={''}>

       <img src={image} alt={'recipe'} className={'object-cover w-full'}/> 
       <div className={''}>  
         <Link to={`/Recipe/${id}`}>
          <h5 className={'text-grey-700 py-3 px-3 text-l font-semibold'}>{title}</h5>
          </Link>
       </div>
    </div>
    </div>
  )
}

export default RecipeColumn;
