function Recipe(props) {  
    const [idPage, setidPage] = useState(null);
    const { id } = useParams();
    const [pageDetails, setpageDetails] = useState({'url' : ``, 'type': ``})


    useEffect(() =>{
            setidPage(null)
            setpageDetails(null)
            const keys = [
              '1882448aefc44b3ea30609cdccf40951',
              '30a751e2874b43f1ad3ecaee77f90358',
              '281e19f37bff48b6a40aebba8c5c3daa',
              '9ec50c8497ff444ebc23caff064ecb84',
              '67f51a29ba7d4d17a05abfe0cd981d80',
              'b996787c904f49ba9142544aa5832a3e',
              'bdd064e1a1d64716b37ff9b6368a03ad',
              'c192100da37d45f7a623175aa6541583',
              'cff3befae479438f8f9dfffcbe5be535',
              'a20b5632aa8d489e82a17c2b6b5c57b9',
              'bdd064e1a1d64716b37ff9b6368a03ad',
              'ebf26298c85c4ab9a16fdcd10992f311',
              'fa37f0fee335472a906bdc5a0cb4c3a8'
                ]
            let key = keys[Math.floor((Math.random() * 13))]

            //Setter section - Set URL request parameters - Everytime URL changes
            if (!id){
                setidPage(false);
                switch(props.type) {
                    case "African":
                      setpageDetails({'url' : `https://api.spoonacular.com/recipes/complexSearch?query=african&number=150&apiKey=${key}`, type : 'Page'})
                      break;

                    case "American":
                      setpageDetails({'url' : `https://api.spoonacular.com/recipes/complexSearch?query=american&number=150&apiKey=${key}`, type : 'Page'})
                      break;

                    case "Chinese":
                      setpageDetails({'url' : `https://api.spoonacular.com/recipes/complexSearch?query=chinese&number=150&apiKey=${key}`, type : 'Page'})
                      break;

                    case "European":
                      setpageDetails({'url' : `https://api.spoonacular.com/recipes/complexSearch?query=european&number=150&apiKey=${key}`, type : 'Page'})
                      break;

                    case "Spanish":
                      setpageDetails({'url' : `https://api.spoonacular.com/recipes/complexSearch?query=spanish&number=150&apiKey=${key}`, type : 'Page'})
                      break;

                    default:
                      break;
                  }    
            }

          if (id){            
                    setidPage(true);
                    setpageDetails({url : `https://api.spoonacular.com/recipes/${id}/information?includeNutrition=true&apiKey=${key}`, type: `Id`})        
          }

    }, [props,id])      


    let {data, loading} = Get(pageDetails) 

if (!idPage) {

  return (
    <div className={''}>

        <div className={' h-full w-full grid mt-64 md:mt-32 lg:grid-cols-4 md:grid-cols-2 gap-10'}>
            {
                loading  ? <Loading/> : Array.isArray(data) && data.map( rec => {
                    return (
                            <RecipeCard key={rec.id} id={rec.id} title={rec.title} summary={rec.summary} cheap={rec.cheap} diets={rec.diets} image={rec.image} />
                    )
                  }
                )
            } 
        </div>
    </div>

  )
} 
else if (idPage) {
  // const TextDivision = document.createElement('div')
  // TextDivision.innerHTML = data.summary;
  // 
      return (
      <div>

        { loading ? <Loading/> : 

           data.image? <div>'Success'</div> : <div>'Fail'</div>
         }
        }

      </div> 
      )
} 



          }

export default Recipe
