import React, { useEffect, useState } from 'react'
import RecipeCard from './RecipeCard';
import Loading from './Loading';
// import { stringify } from 'querystring';

function Recipes() 
{

  const [recipes, setRecipes] = useState([]);
  const [LoadingRecipes, SetLoadingRecipes] = useState(true);

  useEffect( ()=> { 
    const FetchRecipes = async function () {       

      const savedRecipes = localStorage.getItem("Recipes");
      if (savedRecipes) {
           setRecipes(JSON.parse(savedRecipes));
          }

      else {
          let response = await fetch(`https://api.spoonacular.com/recipes/random?number=1500&apiKey=b996787c904f49ba9142544aa5832a3e`);
          let result =  await response.json().then( res => {
                  if (res.status === 'failure'){
                    console.log(res.status);
                  }
                  else{
                  localStorage.setItem("Recipes", JSON.stringify(res.recipes));
                  setRecipes(res.recipes);
                  }
          } )
          .catch(() => {
            console.log("error")
          })
      }
    }
        FetchRecipes()

  }, []);

  useEffect( ()=>{
    if (recipes.length > 1)
    {
      setTimeout(()=> SetLoadingRecipes(false) , 1000) 
    }
  }, [recipes])


  return (
    <div className={''}>
      <div className={''}>
          <div className={'row row-cols-4 g-2'}>
              {
                  LoadingRecipes  ? <Loading/> : recipes.map( rec => {
                      return (
                              <RecipeCard key={rec.id} id={rec.id} title={rec.title} summary={rec.summary} cheap={rec.cheap} diets={rec.diets} image={rec.image} />
                      )
                    }
                  )
              } 
            </div>
      </div>
    </div>
  )
}



export default Recipes
