import { useState, useEffect } from 'react'

function useFetch({ url }) {
  const [reciept, setReciept] = useState({'data' : null, 'loading' : true})
  const savedRecipes = localStorage.getItem("Recipes");

  useEffect( () =>  {       
                        console.log(typeof( url));
                        if (savedRecipes) { 

                            const savedRecipe = JSON.parse(savedRecipes);
                            console.log("The saved is " + savedRecipe);
                            // setReciept({'data':JSON.parse(savedRecipes), 'loading': false})
                            }

                        else 
                            {
                                console.log('here')

                                const response =  fetch(url)
                                {
                                    console.log('working with url');
                                    if (response.ok && response.ok === 'false'){
                                        setReciept({'data' : 'failure', 'loading' : true})
                                    }

                                    else {
                                        localStorage.setItem('Recipes', JSON.stringify(response.recipes))
                                        setReciept({'data': response.recipes, 'loading': false})
                                    }

                                }

                            }

  }, [url])

  return reciept

}

export default useFetch
