import React from 'react'
import { useState } from 'react';
import { useEffect } from 'react'
import {useParams} from 'react-router-dom'
import useFetches from './Utils/Fetches';


function Recipe(props) {  

    const { id } = useParams();
    const [pageDetails, setpageDetails] = useState({'url' : ``})

    useEffect(() =>{

    if (props.type){
        switch(props.type) {
            case "African":
              setpageDetails("African")
              break;

            case "American":
              setpageDetails("American")
              break;

            case "Asian":
              setpageDetails("Asian")
              break;

            default:
              break;
          }    
    }

      if (id){                
        setpageDetails({'url' : `https://api.spoonacular.com/recipes/${id}/information?includeNutrition=true&apiKey=fa37f0fee335472a906bdc5a0cb4c3a8`})        
      }

    }, [])      


    const {data, loading} = useFetches({'url' : pageDetails})



  return (
    <div className={'container section'}> 
        <div className='card-body'>
            <div className={'card'}>
            {/* {pageDetails} */}
            </div>
        </div>
    </div>
  )
}

export default Recipe
