import { useState, useEffect } from 'react'

function useFetch(url) {
  const [reciept, setReciept] = useState({'data' : null, 'loading' : true})
  const savedRecipes = localStorage.getItem("Recipes");

  useEffect( () =>  {       

                        if (savedRecipes) {
                            setReciept({'data':JSON.parse(savedRecipes), 'loading': false})
                            }

                        else 
                        {
                            let response =  fetch(url).then( res => res.JSON()).then( res =>
                                {
                                    if (res.status === 'failure'){
                                        setReciept({'data' : 'failure'})
                                    }
                                    else{
                                        localStorage.setItem("Recipes", JSON.stringify(res.recipes))
                                        setReciept({'data':res.recipes, 'laoding' : false })
                                        }
                            } )

                            // .catch(() => {
                            //     console.log("error")
                            // })
                        }

  }, [url, savedRecipes])

  return reciept

}

export default useFetch
