              'cff3befae479438f8f9dfffcbe5be535',
              'a20b5632aa8d489e82a17c2b6b5c57b9',
              'bdd064e1a1d64716b37ff9b6368a03ad',
              'ebf26298c85c4ab9a16fdcd10992f311',
              'fa37f0fee335472a906bdc5a0cb4c3a8'
                ]
            let key = keys[Math.floor((Math.random() * 20))]

            //Setter section - Set URL request parameters - Everytime URL changes
            if (!id){
                setidPage(false);
                switch(props.type) {
                    case "African":
                      setpageDetails({'url' : `https://api.spoonacular.com/recipes/complexSearch?query=african&number=12&apiKey=${key}`, type : 'Page'})
                      break;

                    case "American":
                      setpageDetails({'url' : `https://api.spoonacular.com/recipes/complexSearch?query=american&number=12&apiKey=${key}`, type : 'Page'})
                      break;

                    case "Chinese":
                      setpageDetails({'url' : `https://api.spoonacular.com/recipes/complexSearch?query=chinese&number=12&apiKey=${key}`, type : 'Page'})
                      break;

                    case "European":
                      setpageDetails({'url' : `https://api.spoonacular.com/recipes/complexSearch?query=european&number=12&apiKey=${key}`, type : 'Page'})
                      break;

                    case "Spanish":
                      setpageDetails({'url' : `https://api.spoonacular.com/recipes/complexSearch?query=spanish&number=12&apiKey=${key}`, type : 'Page'})
                      break;

                    default:
                      break;
                  }    
            }

          if (id){            
                    setidPage(true);
                    setpageDetails({url : `https://api.spoonacular.com/recipes/${id}/information?includeNutrition=true&apiKey=${key}`, type: `Id`})        
          }

    }, [props.type,id])      


    const {data, loading} = Get(pageDetails) 



if (!idPage) {

  return (
    <div className={''}>

        <div className={props.isNavActive ? ' h-full w-full grid pt-20 md:pt-36 lg:pt-20 lg:grid-cols-4 md:grid-cols-3 gap-10' : ' h-full w-full grid pt-72 md:pt-32 lg:pt-20 lg:grid-cols-4 md:grid-cols-3 gap-10'}>
            {
                loading  ? <Loading/> : Array.isArray(data) && data.map( rec => {
                    return (
                            <RecipeCard key={rec.id} id={rec.id} title={rec.title} summary={rec.summary} cheap={rec.cheap} diets={rec.diets} image={rec.image} />
                    )
                  }
                )
            } 
        </div>
    </div>

  )
} 
else if (idPage) {
  // const TextDivision = document.createElement('div')
  // TextDivision.innerHTML = data.summary;
  // 
  //
      return (

      <div>

        { loading ? <Loading/> : 

           data.image ?  
                <div className={props.isNavActive ? "pt-14 pb-4 lg:pt-14 md:pt-28 ml-0 mr-0 flex w-full min-h-screen justify-center items-center" : 'pt-64 pb mt-3 lg:pt-14 md:pt-28 ml-0 mr-0 flex w-full h-screen justify-center items-center'}>
                    <div className='w-2/3 h-2/3 bg-gradient-to-r from-neutral-300 to-stone-400'>
