import React, { useRef } from 'react'
import { useState } from 'react';
import { useEffect } from 'react'
import {useParams} from 'react-router-dom'
import Loading from './Loading';
import RecipeCard from './RecipeCard';

// import useFetch from './Utils/Fetch';
import Get from './Utils/Get';



//A collection of keys are added to maintain uptime
// fa37f0fee335472a906bdc5a0cb4c3a8
// b996787c904f49ba9142544aa5832a3e 
// 1882448aefc44b3ea30609cdccf40951 


function Recipe(props) {  
    const [idPage, setidPage] = useState(null);
    const { id } = useParams();
    const [pageDetails, setpageDetails] = useState({'url' : ``, 'type': ``})

    useEffect(() =>{

            const keys = ['1882448aefc44b3ea30609cdccf40951', 'b996787c904f49ba9142544aa5832a3e', 'fa37f0fee335472a906bdc5a0cb4c3a8']
            let key = keys[Math.floor((Math.random() * 3))]
            if (!id){
                setidPage(false);
                switch(props.type) {
                    case "African":
                      setpageDetails({'url' : `https://api.spoonacular.com/recipes/complexSearch?query=african&number=150&apiKey=${key}`, type : 'Page'})
                      break;

                    case "American":
                      setpageDetails({'url' : `https://api.spoonacular.com/recipes/complexSearch?query=american&number=150&apiKey=${key}`, type : 'Page'})
                      break;

                    case "Chinese":
                      setpageDetails({'url' : `https://api.spoonacular.com/recipes/complexSearch?query=chinese&number=150&apiKey=${key}`, type : 'Page'})
                      break;

                    case "European":
                      setpageDetails({'url' : `https://api.spoonacular.com/recipes/complexSearch?query=european&number=150&apiKey=${key}`, type : 'Page'})
                      break;

                    case "Spanish":
                      setpageDetails({'url' : `https://api.spoonacular.com/recipes/complexSearch?query=spanish&number=150&apiKey=${key}`, type : 'Page'})
                      break;

                    default:
                      break;
                  }    
            }

          if (id){            
                    setidPage(true);
                    setpageDetails({url : `https://api.spoonacular.com/recipes/${id}/information?includeNutrition=true&apiKey=${key}`, type: `Id`})        
          }

    }, [props,id])      


    const {data, loading} = Get(pageDetails) 
    const Description = useRef()


if (!idPage) {

  return (
    <div className={''}>

        <div className={' h-full w-full grid lg:grid-cols-4 md:grid-cols-2 gap-10'}>
            {
                loading  ? <Loading/> : Array.isArray(data) && data.map( rec => {
                    return (
                            <RecipeCard key={rec.id} id={rec.id} title={rec.title} summary={rec.summary} cheap={rec.cheap} diets={rec.diets} image={rec.image} />
                    )
                  }
                )
            } 
        </div>
    </div>

  )
} 
else if (idPage) {
  // const TextDivision = document.createElement('div')
  // TextDivision.innerHTML = data.summary;
  return (
   <div>
     { loading ? <Loading/> : 
          <div className={'w-full h-screen sm:grid grid-cols-2 sm:mt-28 sm:ml-8 sm:p-10 bg-gray-100'}>
              <div className={'min-h-full bg-slate-400'}> 
                  <img src={data.image} alt={'Image'}/>
                  <div>{data.title}</div>
              </div>

              <section className={'bg-white'}>

                  <instructions className={'bg-red-400 w-full'}>How TO</instructions>

                  <description className={'bg-sky-200 w-full'}>
                    <h3  dangerouslySetInnerHTML={{__html: data.summary}} ></h3>
                  </description>

              </section>

          </div>
    }
  </div> 
  )
} 



          }

export default Recipe
